mapper.select:

	select
  	  inquiry_no, user_no, inquiry_title, inquiry_question, to_char(inquiry_date,'YYYY-MM-DD') as inquiry_date,
  	  (select count(inquiry_no) from inquiry_reply where inquiry_no = inquiryList.inquiry_no)
	from(
	    select /*+ INDEX_DESC(inquiry pk_inquiry_no) */
	            rownum as rnum, inquiry_no, user_no inquiry_title, inquiry_question, inquiry_date
    from inquiry
    where 




    rownum <= #{pageNum} * #{amount}
   		)inquiryList
   	WHERE rnum > (#{pageNum} -1) * #{amount}
