mapper.select:
select max(order_no)
from order_list
where user_no = #{user_no}

mapper.delete:
DELETE FROM cart_list 
WHERE cart_id = #{cart_id} 
AND cart_id IN (SELECT cart_id
				FROM cart 
				WHERE user_no = #{cart_id}
				)

mapper.update:
UPDATE cart
SET quantity = quantity - #{quantity}
WHERE cart_id = #{cart_id} AND product_no = #{product_no}
